import React, { useState } from 'react';
import WarningAmberIcon from '@mui/icons-material/WarningAmber';
import rawDataImage from './assets/ingredient2.jpg';

const DemoComponent = () => {
  const [activeTab, setActiveTab] = useState('raw');

  return (
    <div>
     <div className="px-4 lg:px-[6rem] text-5xl text-gray-800 font-jaldi pt-20">
        Demo:
      </div>
    <div className=" mt-20 pt-10 lg:mx-[15rem] mx-[2rem] bg-gray-100 flex flex-col px-4 rounded-[3rem] mb-[4rem]"> 
      <div className="flex flex-col justify-center items-center"> 

        <div className="flex justify-center mb-8 bg-gray-200 rounded-[3rem]"> 
          <button
            className={`px-8 py-4 text-sm md:text-md font-semibold ${activeTab === 'raw' ? 'bg-yellow-400' : 'bg-gray-200'} rounded-[3rem] transition duration-300`}
            onClick={() => setActiveTab('raw')}
          >
            What you see
          </button>
          <button
            className={`px-6 py-2 text-sm md:text-md font-semibold ${activeTab === 'processed' ? 'bg-yellow-400' : 'bg-gray-200'} rounded-[3rem] transition duration-300`} 
            onClick={() => setActiveTab('processed')}
          >
            What we see
          </button>
        </div>

        <div className="w-full max-w-4xl pt-[4rem] pb-[6rem]"> 
        {activeTab === 'raw' ? (
          <img src={rawDataImage} alt="Raw Data" className="mx-auto h-auto max-w-[90%] sm:max-w-[70%] md:max-w-[60%] lg:max-w-[70%] xl:max-w-[40%] rounded-lg" />
        ) : (
          <div className='flex flex-col lg:flex-row justify-center gap-4'>
            <div className='bg-gray-100 p-4 rounded-lg'>
              <AllergenicOptionsComponent />
            </div>
            <div className='bg-gray-100 p-4 rounded-lg'>
              <BetterOptionsComponent />
            </div>
          </div>
        )}
      </div>
    </div>
      </div>
    </div>
  );
};

const ingredients = [
  { name: "Corn Syrup", description: "Linked to obesity, type 2 diabetes, and heart disease", banned: "Sweden" },
  { name: "Titanium Dioxide", description: "Can lead to cell damage, genotoxicity, inflammation, immune response etc.", banned: "European Union(EU)" },
  { name: "Red 40", description: "Contaminated with a cancer causing chemical", banned: "UK, Australia, Norway" },
  { name: "Yellow 5", description: "Linked to hyperactivity and behavioral problems in children.", banned: "Norway, Finland" },
];

const IngredientBox = ({ name, description, banned }) => {
  return (
    <div className="group bg-white rounded-lg py-4 px-10 mb-4 cursor-pointer overflow-hidden font-jaldi w-full">
      <div className="text-lg"> <WarningAmberIcon sx={{color: 'red'}}/>{name}</div>
      <div className="text-sm text-gray-700 max-h-0 group-hover:max-h-96 overflow-hidden transition-max-height duration-500 ease-in-out">
       {description}
      </div>
      <div className="text-sm text-gray-700 max-h-0 group-hover:max-h-96 overflow-hidden transition-max-height duration-500 ease-in-out">
       Banned in: {banned} </div>
    </div>
  );
};

const WhatWeSeeComponent = () => {
  return (
    <div className="w-full flex flex-col items-center">
      {ingredients.map((ingredient, index) => (
        <IngredientBox key={index} name={ingredient.name} description={ingredient.description} banned={ingredient.banned} />
      ))}
    </div>
  );
};

const BetterOptions = [
  { name: "Stevia", description: "A natural sweetener with zero calories.", benefits: "Safe for diabetics" },
  { name: "Zinc Oxide", description: "An essential mineral beneficial for skin health.", benefits: "Supports immune function" },
];

const BetterProductBox = ({ name, description, benefits }) => {
  return (
    <div className="group bg-green-100 rounded-lg py-4 px-10 md:px-5 mb-4 cursor-pointer overflow-hidden font-jaldi w-[80%] mt-4">
      <div className=" text-lg text-green-800">{name}</div>
      <div className="text-sm text-gray-700 max-h-0 group-hover:max-h-96 overflow-hidden transition-max-height duration-500 ease-in-out">
       {description}
      </div>
      <div className="text-sm text-gray-700">
       Benefits: {benefits} </div>
    </div>
  );
};

const BetterOptionsComponent = () => {
  return (
    <div className="w-full flex flex-col items-center bg-white md:mx-10 rounded-lg pt-5">
    <div className='bg-gray-100 p-2 rounded-2xl'><h1 className='font-jaldi font-regular'>A better option for you</h1></div>
      {BetterOptions.map((option, index) => (
        <BetterProductBox key={index} name={option.name} description={option.description} benefits={option.benefits} />
      ))}
    </div>
  );
};
const AllergicIngredients = [
  { name: "Peanut", description: "Common allergen." },
  { name: "Milk", description: "Common allergen." },
];

const AllergenicIngredientBox = ({ name, description }) => {
  return (
    <div className="group bg-red-100 rounded-lg py-4 px-10 md:px-5 mb-4 cursor-pointer overflow-hidden font-jaldi w-[80%] mt-4">
      <div className=" text-lg text-red-800">{name}</div>
      <div className="text-sm text-gray-700">
        {description}
      </div>
    </div>
  );
};
const AllergenicOptionsComponent = () => {
  return (
    <div className="w-full flex flex-col items-center bg-white md:mx-10 rounded-lg pt-5">
      <div className='bg-red-100 p-2 rounded-2xl'><h1 className='font-jaldi font-regular'>Ingredients that may cause allergies</h1></div>
      {AllergicIngredients.map((ingredient, index) => (
        <AllergenicIngredientBox key={index} name={ingredient.name} description={ingredient.description} />
      ))}
    </div>
  );
};



export default DemoComponent;
