import React from 'react';
import appScreen from './assets/Simulator Screenshot - iPhone 15 Pro - 2024-03-22 at 17.44.58.png';
const HomePage = () => {
  return (
    <div className="container mx-auto px-4 py-10">
      <div className="md:flex md:items-center md:justify-between md:mt-[15rem] sm:mt-[2rem]">
        <div className="md:w-1/2 lg:mr-8">
          <h1 className="text-6xl font-bold text-gray-800 mb-12">
            Scan your food ingredients
          </h1>
          <p className="text-2xl text-gray-600 mb-8">
            We make sure that you are eating healthy food by scanning the ingredients of the food you are eating.
          </p>
          <div className="flex flex-col md:flex-row gap-4">
            <button className="bg-black text-white px-6 py-3 rounded-full font-semibold">
              Join Us!
            </button>
          </div>
        </div>


        <div className="md:w-1/2 mt-8 md:mt-0 flex justify-center max-width">

          <div className="shadow-xl border border-gray-200 rounded-3xl overflow-hidden inline-block" style={{ maxWidth: '300px' }}>
            <img 
              src={appScreen} 
              alt="App Screenshot" 
              className="w-full h-auto block" 
              style={{ borderRadius: '36px' }}
            />
          </div>
        </div>
      </div>
    </div>
  );
};

export default HomePage;
