import React, { useState, useRef, useEffect } from 'react';
import { CSSTransition, TransitionGroup } from 'react-transition-group';
import WarningAmberIcon from '@mui/icons-material/WarningAmber';
// import rawDataImage from './assets/ingredient2.jpg';
import receipt from './assets/receipt.png';
import graph from './assets/Graph.png';

const DemoComponent = () => {
  const [activeTab, setActiveTab] = useState('raw');
  const [containerHeight, setContainerHeight] = useState(null);
  const containerRef = useRef(null);
  const endTransitionRef = useRef(null);

  useEffect(() => {

    const currentHeight = containerRef.current ? containerRef.current.offsetHeight : 0;
    setContainerHeight(currentHeight);


    const endTransition = () => {
      const newHeight = containerRef.current ? containerRef.current.offsetHeight : 0;
      setContainerHeight(newHeight);
    };

    endTransitionRef.current = endTransition;  

  }, [activeTab]);

  useEffect(() => {
    const node = containerRef.current;
    if (node) {
      node.addEventListener('transitionend', endTransitionRef.current);


      return () => node.removeEventListener('transitionend', endTransitionRef.current);
    }
  }, []);

  return (
    <div>
     <div className="px-4 lg:px-[6rem] text-5xl text-gray-800 font-jaldi pt-20">
        Demo:
      </div>
    <div className=" mt-20 pt-10 lg:mx-[15rem] mx-[2rem] bg-gray-100 flex flex-col px-4 rounded-[3rem] mb-[4rem]"> 
      <div className="flex flex-col justify-center items-center"> 

        <div className="flex justify-center mb-8 bg-gray-200 rounded-[3rem]"> 
          <button
            className={`px-8 py-4 text-sm md:text-md font-semibold ${activeTab === 'raw' ? 'bg-yellow-400' : 'bg-gray-200'} rounded-[3rem] transition duration-300`}
            onClick={() => setActiveTab('raw')}
          >
            What you see
          </button>
          <button
            className={`px-6 py-2 text-sm md:text-md font-semibold ${activeTab === 'processed' ? 'bg-yellow-400' : 'bg-gray-200'} rounded-[3rem] transition duration-300`} 
            onClick={() => setActiveTab('processed')}
          >
            What we see
          </button>
        </div>

        <div className="transition-container" style={{ height: containerHeight ? `${containerHeight}px` : 'auto' }}>
        <TransitionGroup>
    <CSSTransition
      key={activeTab}
      timeout={300}
      classNames="slide"
    >
        <div >
        {activeTab === 'raw' ? (
          <img src={receipt} alt="Raw Data" className="mx-auto h-auto max-w-[90%] sm:max-w-[70%] md:max-w-[60%] lg:max-w-[70%] xl:max-w-[40%] rounded-lg" />
        ) : (
          <div className='flex flex-col lg:flex-row justify-center gap-4'>
            <div className='w-full lg:w-1/2 bg-gray-100 p-2 rounded-lg'>
              <Graph />
            </div>
            <div className='w-full lg:w-1/2'>
              <div className='bg-gray-100 p-2 rounded-lg mb-4'>
                <Items />
              </div>
              <div className='bg-gray-100 px-2 rounded-lg'>
                <BetterOptionsComponent />
              </div>
            </div>
          </div>
        )}
        </div>
        </CSSTransition>
  </TransitionGroup>
      </div>
    </div>
      </div>
    </div>
  );
};

